mapper.select:
SELECT d.*, x1.CD_NM AS DTM_REASON_NM, x2.CD_NM AS STAT_CD_NM FROM OHR.DTM_HIS d LEFT JOIN OHR.XFRMD2C x1 ON d.DTM_REASON_CD = x1.CD AND x1.CD_KIND = 'DTM_REASON_CD' LEFT JOIN OHR.XFRMD2C x2 ON d.STAT_CD = x2.CD AND x2.CD_KIND = 'ELA_STAT_CD' WHERE d.EMP_ID = #{empId} AND (d.MODI_TYPE != 'D' OR d.MODI_TYPE IS NULL)
AND d.STAT_CD IN
#{item}
AND d.DTM_KIND_CD = #{dtmKindCd}
AND d.DTM_REASON_CD = #{dtmReasonCd}
AND d.STA_YMD >= TO_DATE(#{staYmdInput}, 'YYYY-MM-DD')
AND d.END_YMD <= TO_DATE(#{endYmdInput}, 'YYYY-MM-DD')
ORDER BY d.STAT_CD asc, d.STA_YMD DESC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT count(*) FROM OHR.DTM_HIS d LEFT JOIN OHR.XFRMD2C x1 ON d.DTM_REASON_CD = x1.CD AND x1.CD_KIND = 'DTM_REASON_CD' LEFT JOIN OHR.XFRMD2C x2 ON d.STAT_CD = x2.CD AND x2.CD_KIND = 'ELA_STAT_CD' WHERE d.EMP_ID = #{empId} AND (d.MODI_TYPE != 'D' OR d.MODI_TYPE IS NULL)
AND d.STAT_CD IN
#{item}
AND d.DTM_KIND_CD = #{dtmKindCd}
AND d.DTM_REASON_CD = #{dtmReasonCd}
AND d.STA_YMD >= TO_DATE(#{staYmdInput}, 'YYYY-MM-DD')
AND d.END_YMD <= TO_DATE(#{endYmdInput}, 'YYYY-MM-DD')
